import { Configuration, OpenAIApi } from 'openai';
import data from "./data.json"

import FormSection from './components/FormSection';
import AnswerSection from './components/AnswerSection';
import Table from './components/ProductTable';

import { useState } from 'react';

import axios from 'axios';


const App = () => {

	const [storedValues, setStoredValues] = useState([]);


	const generateResponse = async (newQuestion, setNewQuestion) => {

		try {
			const response = await axios.post('http://localhost:9000/similaritems', { newQuestion });
			if (response.data.botresponse) {
				setStoredValues([
					{
						question: newQuestion,
						answer: response.data.botresponse,
					},
					...storedValues,
				]);
				setNewQuestion('');	
			}

			if (response.data.products) {
				setProductData(productData => ({
					...productData,
					...response.data.products,
				  }));
			}

		  } catch (error) {
			console.error(error);
		  }


	};

	return (
		<div>
			<div className="header-section">
				<h1>Generative AI Retail Product Recommendations demo powered by Astra VectorDB</h1>
				{storedValues.length < 1 && (
					<p>
						Please type what you looking for
					</p>
				)}
			</div>

			<FormSection generateResponse={generateResponse} />

			{storedValues.length > 0 && <AnswerSection storedValues={storedValues} />}

			<tbody>
				<tr>
				<th>Name</th>
				<th>Brand</th>
				<th>Image</th>
				<th>Price</th>
				<th>Rating</th>
				</tr>
				{data.map((item, index) => (
				<tr key={index}>
					<td>{item.title}</td>
					<td>{item.brand}</td>
					<td>
					<img src={item.thumbnail} alt="" height={100} />
					</td>
					<td>{item.price}</td>
					<td>{item.rating}</td>
				</tr>
        		))}
      		</tbody>
		</div>
	);
};

export default App;
